int fulg(int n){
    int cnt[5]={0}; int k=0;
    if(n<100000000 || n>999999999) return 0;
    while(n){ cnt[n%10]++; n/=10; k++; }
    if(k!=9) return 0;
    for(int i=1;i<=4;i++) if(cnt[i]==0) return 0;
    return 1;
}

