#include <iostream>
using namespace std;
long long genMin(int a,int b){
    // a urmat de cifrele lui b
    long long x=a;
    int bb=b, p=1;
    while(bb){ p*=10; bb/=10; }
    long long v1 = x * p + b;

    // cifrele lui b urmate de a
    long long v2 = b;
    int aa=a, q=1;
    while(aa){ q*=10; aa/=10; }
    v2 = v2 * q + a;

    return min(v1,v2);
}
int main(){
    int m,n; cin>>m>>n;
    for(int i=1;i<=m;i++){
        for(int j=1;j<=n;j++){
            cout<<genMin(i,j)<<" ";
        }
        cout<<"\n";
    }
}

